from besser.BUML.notations.source_code_to_buml.output.buml.model import *
from besser.BUML.metamodel.structural import *
from besser.BUML.metamodel.gui import *

ScreenLayout = Layout(type=LayoutType.Flex, orientation="vertical", gap="10px", alignment=JustificationType.Center)
InputFieldColor = Color(background_color="#FFFFFF", text_color="slategrey", border_color="orange")
InputFieldPosition = Position(type=PositionType.Relative)
InputFieldSize = Size(width="100%", height="30px", padding="10px", margin="10px", font_size="2em", unit_size=UnitSize.PIXELS)
InputFieldStyling = Styling(size=InputFieldSize, position=InputFieldPosition, color=InputFieldColor)
ButtonColor = Color(background_color="white", text_color="slategrey", border_color="none")
ButtonPosition = Position(type=PositionType.Relative)
ButtonSize = Size(width="100px", height="40px", padding="5px", margin="10px", font_size="16px", unit_size=UnitSize.PIXELS)
ButtonStyling = Styling(size=ButtonSize, position=ButtonPosition, color=ButtonColor)
ResetButtonColor = Color(background_color="rgba(0, 0, 0, 0)", text_color="white", border_color="none")
ResetButtonStyling = Styling(size=ButtonSize, position=ButtonPosition, color=ResetButtonColor)
SearchResultsColor = Color(background_color="white", text_color="slategrey", border_color="none")
SearchResultsPosition = Position(type=PositionType.Relative)
SearchResultsSize = Size(width="70%", padding="10px", margin="10px auto", unit_size=UnitSize.PIXELS)
SearchResultsStyling = Styling(size=SearchResultsSize, position=SearchResultsPosition, color=SearchResultsColor)
viewComponent: ViewComponent = ViewComponent(name="SearchPageView", description="Search Wikipedia and display results")
searchInputField: InputField = InputField(name="SearchInputField", description="Input field for search word", type="Text", validationRules="", styling=InputFieldStyling)
searchButton: Button = Button(name="SearchButton", description="Button to search", label="Search", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Search, styling=ButtonStyling)
randomButton: Button = Button(name="RandomButton", description="Button to get a random article", label="Random Article", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Navigate, styling=ButtonStyling)
resetButton: Button = Button(name="ResetButton", description="Button to reset search results", label="X", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Cancel, styling=ResetButtonStyling)
datasource_searchResults: DataSourceElement = DataSourceElement(name="SearchResultsDataSource", dataSourceClass=SearchPage, fields=[SearchPage_searchResultsTitle, SearchPage_searchResultsHyperlink, SearchPage_searchResultsDescription])
searchResultsList: DataList = DataList(name="SearchResultsList", description="List of search results", list_sources={datasource_searchResults}, styling=SearchResultsStyling)
SearchPageScreen: Screen = Screen(name="SearchPageScreen", description="Search Wikipedia and display results", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", is_main_page=True, view_elements={searchInputField, searchButton, randomButton, resetButton, searchResultsList}, layout=ScreenLayout)
SearchModule: Module = Module(name="SearchModule", screens={SearchPageScreen})
gui_model: GUIModel = GUIModel(name="WikipediaSearchApp", package="com.example.wikipedia", versionCode="1", versionName="1.0", description="An application to search Wikipedia articles.", screenCompatibility=True, modules={SearchModule})
