from besser.BUML.notations.source_code_to_buml.output.buml.model import *
from besser.BUML.metamodel.structural import *
from besser.BUML.metamodel.gui import *

ScreenLayout = Layout(type=LayoutType.Flex, orientation="vertical", gap="15px", alignment=JustificationType.Center)
ButtonColor = Color(background_color="#27AE60", text_color="#FFFFFF", border_color="#0056b3")
buttonPosition = Position(type=PositionType.Relative, z_index=10)
buttonSize = Size(height="40px", padding="10px", font_size="14px", unit_size=UnitSize.PIXELS)
buttonStyling = Styling(size=buttonSize, position=buttonPosition, color=ButtonColor)
viewComponent = ViewComponent(name="MultiStepForm", description="A multi-step form for user account setup, social profiles, and personal details")
inputFieldSize = Size(width="100%", padding="15px", font_size="13px", unit_size=UnitSize.PIXELS)
inputFieldColor = Color(background_color="#FFFFFF", text_color="#2C3E50", border_color="#CCCCCC")
inputFieldStyling = Styling(size=inputFieldSize, color=inputFieldColor)
emailField = InputField(name="EmailField", description="User email input", type=InputFieldType.Email, styling=inputFieldStyling)
passwordField = InputField(name="PasswordField", description="User password input", type=InputFieldType.Password, styling=inputFieldStyling)
confirmPasswordField = InputField(name="ConfirmPasswordField", description="Confirm password input", type=InputFieldType.Password, styling=inputFieldStyling)
twitterField = InputField(name="TwitterField", description="Twitter profile input", type=InputFieldType.Text, styling=inputFieldStyling)
facebookField = InputField(name="FacebookField", description="Facebook profile input", type=InputFieldType.Text, styling=inputFieldStyling)
gplusField = InputField(name="GooglePlusField", description="Google Plus profile input", type=InputFieldType.Text, styling=inputFieldStyling)
fnameField = InputField(name="FirstNameField", description="First name input", type=InputFieldType.Text, styling=inputFieldStyling)
lnameField = InputField(name="LastNameField", description="Last name input", type=InputFieldType.Text, styling=inputFieldStyling)
phoneField = InputField(name="PhoneField", description="Phone number input", type=InputFieldType.Tel, styling=inputFieldStyling)
addressField = InputField(name="AddressField", description="Address input", type=InputFieldType.Text, styling=inputFieldStyling)
nextButton1 = Button(name="NextButton1", description="Proceed to social profiles", label="Next", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Next, styling=buttonStyling)
nextButton2 = Button(name="NextButton2", description="Proceed to personal details", label="Next", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Next, styling=buttonStyling)
previousButton1 = Button(name="PreviousButton1", description="Return to account setup", label="Previous", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Back, styling=buttonStyling)
previousButton2 = Button(name="PreviousButton2", description="Return to social profiles", label="Previous", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Back, styling=buttonStyling)
submitButton = Button(name="SubmitButton", description="Submit the form", label="Submit", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Submit, styling=buttonStyling)
formStyling = Styling(size=Size(width="80%", padding="20px 30px", unit_size=UnitSize.PIXELS), color=Color(background_color="#FFFFFF", text_color="", border_color=""))
accountSetupForm = Form(name="AccountSetupForm", description="Form for account setup", inputFields={emailField, passwordField, confirmPasswordField}, styling=formStyling)
socialProfilesForm = Form(name="SocialProfilesForm", description="Form for social profiles", inputFields={twitterField, facebookField, gplusField}, styling=formStyling)
personalDetailsForm = Form(name="PersonalDetailsForm", description="Form for personal details", inputFields={fnameField, lnameField, phoneField, addressField}, styling=formStyling)
accountSetupScreen = Screen(name="AccountSetupScreen", description="Screen for account setup", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", view_elements={accountSetupForm, nextButton1}, layout=ScreenLayout, is_main_page=True)
socialProfilesScreen = Screen(name="SocialProfilesScreen", description="Screen for social profiles", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", view_elements={socialProfilesForm, previousButton1, nextButton2}, layout=ScreenLayout, is_main_page=False)
personalDetailsScreen = Screen(name="PersonalDetailsScreen", description="Screen for personal details", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", view_elements={personalDetailsForm, previousButton2, submitButton}, layout=ScreenLayout, is_main_page=False)
multiStepFormModule = Module(name="MultiStepFormModule", screens={accountSetupScreen, socialProfilesScreen, personalDetailsScreen})
gui_model = GUIModel(name="MultiStepFormApp", package="com.example.multistepform", versionCode="1", versionName="1.0", description="A multi-step form application for user registration.", screenCompatibility=True, modules={multiStepFormModule})
