from besser.BUML.notations.source_code_to_buml.output.buml.model import *
from besser.BUML.metamodel.structural import *
from besser.BUML.metamodel.gui import *

ScreenLayout = Layout(type=LayoutType.Flex, orientation="vertical", gap="15px", alignment=JustificationType.Center)
ButtonColor = Color(background_color="#9b2", text_color="#140", border_color="#471")
buttonPosition = Position(type=PositionType.Relative, alignment="center", z_index=10)
buttonSize = Size(width="75px", height="30px", padding="0", margin="0", font_size="12px", unit_size=UnitSize.PIXELS)
buttonStyling = Styling(size=buttonSize, position=buttonPosition, color=ButtonColor)
InputFieldColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="#CCCCCC")
inputFieldPosition = Position(type=PositionType.Relative, alignment="center", z_index=0)
inputFieldSize = Size(width="225px", height="30px", padding="0 5px", margin="0", font_size="12px", unit_size=UnitSize.PIXELS)
inputFieldStyling = Styling(size=inputFieldSize, position=inputFieldPosition, color=InputFieldColor)
viewComponent = ViewComponent(name="LoginView", description="User login and registration form")
usernameField = InputField(name="UsernameField", description="Enter your username", type="Text", validationRules="", styling=inputFieldStyling)
passwordField = InputField(name="PasswordField", description="Enter your password", type="Password", validationRules="", styling=inputFieldStyling)
firstNameField = InputField(name="FirstNameField", description="Enter your first name", type="Text", validationRules="", styling=inputFieldStyling)
lastNameField = InputField(name="LastNameField", description="Enter your last name", type="Text", validationRules="", styling=inputFieldStyling)
emailField = InputField(name="EmailField", description="Enter your email", type="Email", validationRules="", styling=inputFieldStyling)
confirmEmailField = InputField(name="ConfirmEmailField", description="Confirm your email", type="Email", validationRules="", styling=inputFieldStyling)
captchaField = InputField(name="CaptchaField", description="Solve the captcha", type="Text", validationRules="", styling=inputFieldStyling)
loginButton = Button(name="LoginButton", description="Submit login form", label="Login", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Login, styling=buttonStyling)
registerButton = Button(name="RegisterButton", description="Navigate to registration", label="Register!", buttonType=ButtonType.TextButton, actionType=ButtonActionType.Navigate, styling=buttonStyling)
forgotPasswordButton = Button(name="ForgotPasswordButton", description="Forgot password", label="Forgot?", buttonType=ButtonType.TextButton, actionType=ButtonActionType.Help, styling=buttonStyling)
loginForm = Form(name="LoginForm", description="Form for user login", inputFields={usernameField, passwordField}, styling=None)
registerForm = Form(name="RegisterForm", description="Form for user registration", inputFields={firstNameField, lastNameField, emailField, confirmEmailField, captchaField}, styling=None)
LoginScreen = Screen(name="LoginScreen", description="User login and registration screen", x_dpi="160", y_dpi="160", screen_size="Medium", view_elements={loginForm, registerForm, loginButton, registerButton, forgotPasswordButton}, is_main_page=True, layout=ScreenLayout)
LoginModule = Module(name="LoginModule", screens={LoginScreen})
gui_model = GUIModel(name="LoginApp", package="com.example.loginapp", versionCode="1", versionName="1.0", description="A simple login application", screenCompatibility=True, modules={LoginModule})
