from besser.BUML.notations.source_code_to_buml.output.buml.model import *
from besser.BUML.metamodel.structural import *
from besser.BUML.metamodel.gui import *

ScreenLayout = Layout(type=LayoutType.Flex, orientation="vertical", gap="15px", alignment=JustificationType.Center)
inputFieldColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="#CCCCCC")
inputFieldPosition = Position(type=PositionType.Relative, z_index=0)
inputFieldSize = Size(width="100%", height="40px", padding="10px", margin="5px", font_size="14px", unit_size=UnitSize.PIXELS)
inputFieldStyling = Styling(size=inputFieldSize, position=inputFieldPosition, color=inputFieldColor)
buttonColor = Color(background_color="#981e32", text_color="#FFFFFF", border_color="#FFA000")
buttonPosition = Position(type=PositionType.Relative, z_index=0)
buttonSize = Size(width="100%", height="40px", padding="10px", margin="5px", font_size="14px", unit_size=UnitSize.PIXELS)
buttonStyling = Styling(size=buttonSize, position=buttonPosition, color=buttonColor)
viewComponent = ViewComponent(name="InventoryFormView", description="Form to add items to inventory")
serialInput = InputField(name="SerialNumberInput", description="Input for serial number", type=InputFieldType.Text, styling=inputFieldStyling)
itemNameInput = InputField(name="ItemNameInput", description="Input for item name", type=InputFieldType.Text, validationRules="required", styling=inputFieldStyling)
subtypeInput = InputField(name="SubtypeInput", description="Input for subtype", type=InputFieldType.Text, styling=inputFieldStyling)
typeInput = InputField(name="TypeInput", description="Input for type", type=InputFieldType.Text, styling=inputFieldStyling)
assignedToInput = InputField(name="AssignedToInput", description="Input for assignee's name", type=InputFieldType.Text, styling=inputFieldStyling)
locationInput = InputField(name="LocationInput", description="Input for item's location", type=InputFieldType.Text, styling=inputFieldStyling)
quantityInput = InputField(name="QuantityInput", description="Input for number in stock", type=InputFieldType.Number, styling=inputFieldStyling)
macAddressInput = InputField(name="MacAddressInput", description="Input for MAC address", type=InputFieldType.Text, styling=inputFieldStyling)
ipAddressInput = InputField(name="IpAddressInput", description="Input for IP address", type=InputFieldType.Text, styling=inputFieldStyling)
checkoutYesRadio = InputField(name="CheckoutYesRadio", description="Radio button for checkoutable yes", type=InputFieldType.Text, styling=inputFieldStyling)
checkoutNoRadio = InputField(name="CheckoutNoRadio", description="Radio button for checkoutable no", type=InputFieldType.Text, styling=inputFieldStyling)
inventoryForm = Form(name="InventoryForm", description="Form to add inventory items", inputFields={serialInput, itemNameInput, subtypeInput, typeInput, assignedToInput, locationInput, quantityInput, macAddressInput, ipAddressInput, checkoutYesRadio, checkoutNoRadio}, styling=None)
submitButton = Button(name="SubmitButton", description="Submit the form", label="Send", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.SubmitForm, styling=buttonStyling)
InventoryScreen = Screen(name="InventoryScreen", description="Screen to add items to inventory", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", view_elements={inventoryForm, submitButton}, is_main_page=True, layout=ScreenLayout)
InventoryModule = Module(name="InventoryModule", screens={InventoryScreen})
gui_model = GUIModel(name="InventoryManagementApp", package="com.example.inventorymanagement", versionCode="1", versionName="1.0", description="Application for managing inventory items.", screenCompatibility=True, modules={InventoryModule})
