from besser.BUML.notations.source_code_to_buml.output.buml.model import *
from besser.BUML.metamodel.structural import *
from besser.BUML.metamodel.gui import *

ScreenLayout = Layout(type=LayoutType.Flex, orientation="vertical", gap="15px", alignment=JustificationType.Center)
ButtonColor = Color(background_color="#6c757d", text_color="#FFFFFF", border_color="#5a6268")
buttonPosition = Position(type=PositionType.Relative, z_index=10)
buttonSize = Size(width="auto", height="40px", padding="8px", margin="10px", font_size="14px", unit_size=UnitSize.PIXELS)
buttonStyling = Styling(size=buttonSize, position=buttonPosition, color=ButtonColor)
DropdownColor = Color(background_color="#FFFFFF", text_color="#343A40", border_color="#CED4DA")
dropdownPosition = Position(type=PositionType.Relative)
dropdownSize = Size(width="100%", padding="10px", unit_size=UnitSize.PIXELS)
dropdownStyling = Styling(size=dropdownSize, position=dropdownPosition, color=DropdownColor)
sidenavColor = Color(background_color="#111", text_color="#818181", border_color="#111")
sidenavPosition = Position(type=PositionType.Fixed, top="0", left="0", z_index=1)
sidenavSize = Size(width="180px", height="100%", padding="20px", unit_size=UnitSize.PIXELS)
sidenavStyling = Styling(size=sidenavSize, position=sidenavPosition, color=sidenavColor)
headerPosition = Position(type=PositionType.Relative)
headerSize = Size(width="100%", padding="10px", unit_size=UnitSize.PIXELS)
headerStyling = Styling(size=headerSize, position=headerPosition, color=None)
viewComponent = ViewComponent(name="InventoryView", description="Display inventory with actions")
sidenavMenu = Menu(name="SidenavMenu", description="Side navigation menu", menuItems={MenuItem(label="Inventory"), MenuItem(label="Transactions"), MenuItem(label="Settings")}, styling=sidenavStyling)
headerTitle = ViewComponent(name="HeaderTitle", description="Page title", styling=headerStyling)
addProductButton = Button(name="AddProductButton", description="Add a new product", label="Add Product", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Add, styling=buttonStyling)
productDataSource = DataSourceElement(name="ProductDataSource", dataSourceClass=Product, fields={Product_name, Product_category})
productTable = DataList(name="ProductTable", description="List of products", list_sources={productDataSource}, styling=dropdownStyling)
actionDropdown = Menu(name="ActionDropdown", description="Actions for products", menuItems={MenuItem(label="Details"), MenuItem(label="Add"), MenuItem(label="Remove")}, styling=dropdownStyling)
InventoryPageScreen = Screen(name="InventoryPage", description="Manage inventory products", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", view_elements={sidenavMenu, headerTitle, addProductButton, productTable, actionDropdown}, is_main_page=True, layout=ScreenLayout)
InventoryModule = Module(name="InventoryModule", screens={InventoryPageScreen})
gui_model = GUIModel(name="InventoryManagementApp", package="com.example.inventorymanagement", versionCode="1", versionName="1.0", description="A web application for managing inventory.", screenCompatibility=True, modules={InventoryModule})
