from besser.BUML.notations.source_code_to_buml.output.buml.model import *
from besser.BUML.metamodel.structural import *
from besser.BUML.metamodel.gui import *

ScreenLayout = Layout(type=LayoutType.Flex, orientation="vertical", gap="15px", alignment=JustificationType.Center)
ButtonColor = Color(background_color="#007BFF", text_color="#FFFFFF", border_color="#0056b3")
buttonPosition = Position(type=PositionType.Relative, z_index=10)
buttonSize = Size(height="40px", padding="12px", font_size="14px", unit_size=UnitSize.PIXELS)
buttonStyling = Styling(size=buttonSize, position=buttonPosition, color=ButtonColor)
inputFieldColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="#CCCCCC")
inputFieldPosition = Position(type=PositionType.Relative)
inputFieldSize = Size(width="100%", height="auto", padding="8px", unit_size=UnitSize.PIXELS)
inputFieldStyling = Styling(size=inputFieldSize, position=inputFieldPosition, color=inputFieldColor)
formColor = Color(background_color="#1e002acc", text_color="#FFFFFF", border_color="#DDDDDD")
formPosition = Position(type=PositionType.Relative)
formSize = Size(width="600px", height="auto", padding="48px 36px", unit_size=UnitSize.PIXELS)
formStyling = Styling(size=formSize, position=formPosition, color=formColor)
viewComponent: ViewComponent = ViewComponent(name="SurveyFormView", description="Survey form for user feedback")
nameInput: InputField = InputField(name="NameInput", description="Enter your name", type="Text", validationRules="required", styling=inputFieldStyling)
emailInput: InputField = InputField(name="EmailInput", description="Enter your email", type="Email", validationRules="required", styling=inputFieldStyling)
ageInput: InputField = InputField(name="AgeInput", description="Enter your age", type="Number", validationRules="optional", styling=inputFieldStyling)
roleDropdown: InputField = InputField(name="RoleDropdown", description="Select your role", type="Text", validationRules="required", styling=inputFieldStyling)
recommendRadio: InputField = InputField(name="RecommendRadio", description="Would you recommend us?", type="Text", validationRules="required", styling=inputFieldStyling)
serviceDropdown: InputField = InputField(name="ServiceDropdown", description="Select a service", type="Text", validationRules="optional", styling=inputFieldStyling)
improvementCheckbox: InputField = InputField(name="ImprovementCheckbox", description="Select improvements", type="Text", validationRules="optional", styling=inputFieldStyling)
commentsTextarea: InputField = InputField(name="CommentsTextarea", description="Enter comments", type="Text", validationRules="optional", styling=inputFieldStyling)
submitButton: Button = Button(name="SubmitButton", description="Submit the form", label="Submit", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Submit, styling=buttonStyling)
surveyForm: Form = Form(name="SurveyForm", description="User feedback survey form", inputFields={nameInput, emailInput, ageInput, roleDropdown, recommendRadio, serviceDropdown, improvementCheckbox, commentsTextarea}, styling=formStyling)
SurveyFormScreen: Screen = Screen(name="SurveyFormScreen", description="Screen for user feedback survey", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", is_main_page=True, view_elements={surveyForm, submitButton}, layout=ScreenLayout)
SurveyModule: Module = Module(name="SurveyModule", screens={SurveyFormScreen})
gui_model: GUIModel = GUIModel(name="SurveyApp", package="com.example.surveyapp", versionCode="1", versionName="1.0", description="A web application for collecting user feedback.", screenCompatibility=True, modules={SurveyModule})
