from besser.BUML.notations.source_code_to_buml.output.buml.model import *
from besser.BUML.metamodel.structural import *
from besser.BUML.metamodel.gui import *

ScreenLayout = Layout(type=LayoutType.Flex, orientation="vertical", gap="15px", alignment=JustificationType.Center)
inputFieldColor = Color(background_color="#0a0a23", text_color="#ffffff", border_color="#0a0a23")
inputFieldPosition = Position(type=PositionType.Relative, z_index=0)
inputFieldSize = Size(width="100%", height="40px", padding="8px", margin="10px", font_size="16px", unit_size=UnitSize.PIXELS)
inputFieldStyling = Styling(size=inputFieldSize, position=inputFieldPosition, color=inputFieldColor)
submitButtonColor = Color(background_color="#3b3b4f", text_color="#FFFFFF", border_color="white")
submitButtonPosition = Position(type=PositionType.Relative, z_index=0)
submitButtonSize = Size(width="60%", height="40px", padding="8px", margin="10px auto", font_size="1.1rem", unit_size=UnitSize.PIXELS)
submitButtonStyling = Styling(size=submitButtonSize, position=submitButtonPosition, color=submitButtonColor)
viewComponent = ViewComponent(name="RegistrationFormView", description="User registration form")
firstNameField = InputField(name="FirstNameField", description="Enter your first name", type=InputFieldType.Text, validationRules="required", styling=inputFieldStyling)
lastNameField = InputField(name="LastNameField", description="Enter your last name", type=InputFieldType.Text, validationRules="required", styling=inputFieldStyling)
emailField = InputField(name="EmailField", description="Enter your email", type=InputFieldType.Email, validationRules="required", styling=inputFieldStyling)
passwordField = InputField(name="PasswordField", description="Create a new password", type=InputFieldType.Password, validationRules="required pattern=[a-z0-5]{8,}", styling=inputFieldStyling)
ageField = InputField(name="AgeField", description="Input your age", type=InputFieldType.Number, validationRules="min=13 max=120", styling=inputFieldStyling)
bioField = InputField(name="BioField", description="Provide a bio", type=InputFieldType.Text, validationRules="", styling=inputFieldStyling)
profilePictureField = InputField(name="ProfilePictureField", description="Upload a profile picture", type=InputFieldType.File, validationRules="", styling=inputFieldStyling)
accountTypeField = InputField(name="AccountTypeField", description="Select account type", type=InputFieldType.Text, validationRules="required", styling=inputFieldStyling)
referrerField = InputField(name="ReferrerField", description="How did you hear about us?", type=InputFieldType.Text, validationRules="", styling=inputFieldStyling)
termsAndConditionsField = InputField(name="TermsAndConditionsField", description="Accept terms and conditions", type=InputFieldType.Text, validationRules="required", styling=inputFieldStyling)
registrationForm = Form(name="RegistrationForm", description="User registration form", inputFields={firstNameField, lastNameField, emailField, passwordField, ageField, bioField, profilePictureField, accountTypeField, referrerField, termsAndConditionsField}, styling=None)
submitButton = Button(name="SubmitButton", description="Submit the form", label="Submit", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.SubmitForm, styling=submitButtonStyling)
RegistrationScreen = Screen(name="RegistrationScreen", description="User registration screen", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", view_elements={registrationForm, submitButton}, is_main_page=True, layout=ScreenLayout)
RegistrationModule = Module(name="RegistrationModule", screens={RegistrationScreen})
gui_model = GUIModel(name="RegistrationApp", package="com.example.registrationapp", versionCode="1", versionName="1.0", description="A user registration application", screenCompatibility=True, modules={RegistrationModule})
