from besser.BUML.notations.source_code_to_buml.output.buml.model import *
from besser.BUML.metamodel.structural import *
from besser.BUML.metamodel.gui import *

ScreenLayout = Layout(type=LayoutType.Flex, orientation="vertical", gap="15px", alignment=JustificationType.Center)
ButtonColor = Color(background_color="#FDD835", text_color="#FFFFFF", border_color="#FBC02D")
ArchiveButtonColor = Color(background_color="#D32F2F", text_color="#FFFFFF", border_color="#C62828")
AddButtonColor = Color(background_color="#388E3C", text_color="#FFFFFF", border_color="#2E7D32")
RestoreButtonColor = Color(background_color="#1976D2", text_color="#FFFFFF", border_color="#1565C0")
DeleteButtonColor = Color(background_color="#D32F2F", text_color="#FFFFFF", border_color="#C62828")
buttonPosition = Position(type=PositionType.Relative, z_index=10)
buttonSize = Size(width="100px", height="40px", padding="8px", margin="24px", font_size="14px", icon_size="24px", unit_size=UnitSize.PIXELS)
EditButtonStyling = Styling(size=buttonSize, position=buttonPosition, color=ButtonColor)
ArchiveButtonStyling = Styling(size=buttonSize, position=buttonPosition, color=ArchiveButtonColor)
AddButtonStyling = Styling(size=buttonSize, position=buttonPosition, color=AddButtonColor)
RestoreButtonStyling = Styling(size=buttonSize, position=buttonPosition, color=RestoreButtonColor)
DeleteButtonStyling = Styling(size=buttonSize, position=buttonPosition, color=DeleteButtonColor)
InputFieldColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="#BDBDBD")
InputFieldPosition = Position(type=PositionType.Relative, z_index=0)
InputFieldSize = Size(width="200px", height="40px", padding="8px", margin="24px", font_size="14px", unit_size=UnitSize.PIXELS)
InputFieldStyling = Styling(size=InputFieldSize, position=InputFieldPosition, color=InputFieldColor)
viewComponent = ViewComponent(name="PersonListView", description="Display a list of persons with actions")
editButton = Button(name="EditButton", description="Edit a person", label="Edit", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Edit, styling=EditButtonStyling)
archiveButton = Button(name="ArchiveButton", description="Archive a person", label="Archive", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Delete, styling=ArchiveButtonStyling)
addButton = Button(name="AddButton", description="Add a person", label="Add", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Add, styling=AddButtonStyling)
restoreButton = Button(name="RestoreButton", description="Restore a person", label="Restore", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Add, styling=RestoreButtonStyling)
deleteButton = Button(name="DeleteButton", description="Delete a person", label="Delete", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Delete, styling=DeleteButtonStyling)
lnameInput = InputField(name="LastNameInput", description="Input for last name", type="Text", validationRules="", styling=InputFieldStyling)
fnameInput = InputField(name="FirstNameInput", description="Input for first name", type="Text", validationRules="", styling=InputFieldStyling)
ageInput = InputField(name="AgeInput", description="Input for age", type="Number", validationRules="", styling=InputFieldStyling)
jobInput = InputField(name="JobInput", description="Input for job", type="Text", validationRules="", styling=InputFieldStyling)
addressInput = InputField(name="AddressInput", description="Input for address", type="Text", validationRules="", styling=InputFieldStyling)
datasource_person = DataSourceElement(name="PersonDataSource", dataSourceClass=Person, fields=[Person_lname, Person_fname, Person_age, Person_job, Person_address])
personList = DataList(name="PersonList", description="A list of persons", list_sources={datasource_person}, styling=InputFieldStyling)
PersonListScreen = Screen(name="PersonListScreen", description="Manage a list of persons", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", is_main_page=True, view_elements={editButton, archiveButton, addButton, restoreButton, deleteButton, lnameInput, fnameInput, ageInput, jobInput, addressInput, personList}, layout=ScreenLayout)
MyModule = Module(name="PersonManagementModule", screens={PersonListScreen})
gui_model = GUIModel(name="PersonManagementApp", package="com.example.personmanagement", versionCode="1", versionName="1.0", description="A web application for managing persons.", screenCompatibility=True, modules={MyModule})
