from besser.BUML.notations.source_code_to_buml.output.buml.model import *
from besser.BUML.metamodel.structural import *
from besser.BUML.metamodel.gui import *

ScreenLayout = Layout(type=LayoutType.Flex, orientation="vertical", gap="15px", alignment=JustificationType.Center)
HeaderColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="#CCCCCC")
HeaderPosition = Position(type=PositionType.Relative, alignment="center")
HeaderSize = Size(width="100%", height="auto", padding="10px", font_size="24px", unit_size=UnitSize.PIXELS)
HeaderStyling = Styling(size=HeaderSize, position=HeaderPosition, color=HeaderColor)
NavColor = Color(background_color="#FFFFFF", text_color="#FF69B4", border_color="#CCCCCC")
NavPosition = Position(type=PositionType.Relative, alignment="center")
NavSize = Size(width="100%", height="auto", padding="10px", font_size="16px", unit_size=UnitSize.PIXELS)
NavStyling = Styling(size=NavSize, position=NavPosition, color=NavColor)
SectionColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="#CCCCCC")
SectionPosition = Position(type=PositionType.Relative, alignment="center")
SectionSize = Size(width="100%", height="auto", padding="10px", font_size="18px", unit_size=UnitSize.PIXELS)
SectionStyling = Styling(size=SectionSize, position=SectionPosition, color=SectionColor)
ButtonColor = Color(background_color="#FF69B4", text_color="#FFFFFF", border_color="#FF1493")
ButtonPosition = Position(type=PositionType.Relative, alignment="right")
ButtonSize = Size(width="auto", height="40px", padding="10px", font_size="16px", unit_size=UnitSize.PIXELS)
ButtonStyling = Styling(size=ButtonSize, position=ButtonPosition, color=ButtonColor)
headerComponent = ViewComponent(name="Header", description="Travel Agency Header", styling=HeaderStyling)
navComponent = Menu(name="Navigation", description="Main Navigation", menuItems={MenuItem(label=Property(name="Flights", type=Flights)), MenuItem(label=Property(name="Hotels", type=Hotels)), MenuItem(label=Property(name="Tours", type=Tours)), MenuItem(label=Property(name="Packages", type=Packages))}, styling=NavStyling)
flightsSection = ViewComponent(name="FlightsSection", description="Flights Information", styling=SectionStyling)
hotelsSection = ViewComponent(name="HotelsSection", description="Hotels Information", styling=SectionStyling)
toursSection = ViewComponent(name="ToursSection", description="Tours Information", styling=SectionStyling)
packagesSection = ViewComponent(name="PackagesSection", description="Packages Information", styling=SectionStyling)
bookNowButton = Button(name="BookNowButton", description="Book Now Button", label="Book Now", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Navigate, styling=ButtonStyling)
TravelAgencyScreen = Screen(name="TravelAgencyScreen", description="Travel Agency Main Screen", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", view_elements={headerComponent, navComponent, flightsSection, hotelsSection, toursSection, packagesSection, bookNowButton}, is_main_page=True, layout=ScreenLayout)
TravelAgencyModule = Module(name="TravelAgencyModule", screens={TravelAgencyScreen})
gui_model = GUIModel(name="TravelAgencyApp", package="com.example.travelagency", versionCode="1", versionName="1.0", description="A web application for managing travel agency services.", screenCompatibility=True, modules={TravelAgencyModule})
