from besser.BUML.notations.source_code_to_buml.output.buml.model import *
from besser.BUML.metamodel.structural import *
from besser.BUML.metamodel.gui import *

ScreenLayout = Layout(type=LayoutType.Flex, orientation="vertical", gap="15px", alignment=JustificationType.Center)
HeaderColor = Color(background_color="#6B7280", text_color="#FFFFFF", border_color="")
HeaderPosition = Position(type=PositionType.Relative, alignment=JustificationType.Center)
HeaderSize = Size(width="100%", height="auto", padding="16px", font_size="24", unit_size=UnitSize.PIXELS)
HeaderStyling = Styling(size=HeaderSize, position=HeaderPosition, color=HeaderColor)
ButtonColor = Color(background_color="#FFFFFF", text_color="#6B7280", border_color="#E5E7EB")
ButtonPosition = Position(type=PositionType.Relative)
ButtonSize = Size(width="", height="40", padding="8px", margin="8", font_size="14", unit_size=UnitSize.PIXELS)
ButtonStyling = Styling(size=ButtonSize, position=ButtonPosition, color=ButtonColor)
TableColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="#E5E7EB")
TablePosition = Position(type=PositionType.Relative)
TableSize = Size(width="100%", height="auto", padding="8px", margin="16", font_size="16", unit_size=UnitSize.PIXELS)
TableStyling = Styling(size=TableSize, position=TablePosition, color=TableColor)
FooterColor = Color(background_color="#6B7280", text_color="#FFFFFF", border_color="")
FooterPosition = Position(type=PositionType.Relative)
FooterSize = Size(width="100%", height="auto", padding="16px", font_size="14", unit_size=UnitSize.PIXELS)
FooterStyling = Styling(size=FooterSize, position=FooterPosition, color=FooterColor)
header: ViewComponent = ViewComponent(name="Header", description="Header of the Banking Institution", styling=HeaderStyling)
navMenu: Menu = Menu(name="NavigationMenu", description="Navigation menu with links", menuItems={MenuItem(label="Home"), MenuItem(label="About"), MenuItem(label="Contact")}, styling=ButtonStyling)
openAccountButton: Button = Button(name="OpenAccountButton", description="Button to open a new account", label="Open Account", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Navigate, styling=ButtonStyling)
mainContent: ViewComponent = ViewComponent(name="MainContent", description="Main content area", styling=TableStyling)
servicesTable: DataList = DataList(name="ServicesTable", description="Table of services offered", list_sources={DataSourceElement(name="ServiceDataSource", dataSourceClass=Service, fields={Service_serviceName, Service_fee})}, styling=TableStyling)
footer: ViewComponent = ViewComponent(name="Footer", description="Footer with contact information", styling=FooterStyling)
BankingInstitutionScreen: Screen = Screen(name="BankingInstitutionScreen", description="Screen for the Banking Institution", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", view_elements={header, navMenu, openAccountButton, mainContent, servicesTable, footer}, is_main_page=True, layout=ScreenLayout)
BankingModule: Module = Module(name="BankingModule", screens={BankingInstitutionScreen})
gui_model: GUIModel = GUIModel(name="BankingApp", package="com.example.bankingapp", versionCode="1", versionName="1.0", description="A web application for managing banking services.", screenCompatibility=True, modules={BankingModule})
