HeaderStyling = Styling(size=HeaderSize, position=HeaderPosition, color=HeaderColor)
SectionColor = Color(background_color="#2D3748", text_color="#FFFFFF", border_color="")
SectionPosition = Position(type=PositionType.Relative)
SectionSize = Size(width="50%", height="auto", padding="16px", font_size="16", unit_size=UnitSize.PIXELS)
SectionStyling = Styling(size=SectionSize, position=SectionPosition, color=SectionColor)
ButtonColor = Color(background_color="#4A5568", text_color="#FFFFFF", border_color="#2D3748")
ButtonPosition = Position(type=PositionType.Relative)
ButtonSize = Size(width="auto", height="40px", padding="8px", margin="16px", font_size="14", unit_size=UnitSize.PIXELS)
ButtonStyling = Styling(size=ButtonSize, position=ButtonPosition, color=ButtonColor)
FooterColor = Color(background_color="#1A202C", text_color="#FFFFFF", border_color="")
