InputFieldStyling = Styling(size=InputFieldSize, position=InputFieldPosition, color=InputFieldColor)
viewComponent: ViewComponent = ViewComponent(name="ConstructionCompanyPageView", description="Display construction company information")
header: ViewComponent = ViewComponent(name="Header", description="Header of the page", styling=HeaderStyling)
section1: ViewComponent = ViewComponent(name="Section1", description="First section with construction image", styling=SectionStyling)
section2: ViewComponent = ViewComponent(name="Section2", description="Second section with architecture image", styling=SectionStyling)
learnMoreButton1: Button = Button(name="LearnMoreButton1", description="Learn more about construction", label="Learn More", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Navigate, styling=ButtonStyling)
learnMoreButton2: Button = Button(name="LearnMoreButton2", description="Learn more about architecture", label="Learn More", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Navigate, styling=ButtonStyling)
footer: ViewComponent = ViewComponent(name="Footer", description="Footer with contact form", styling=FooterStyling)
