nameField: InputField = InputField(name="NameField", description="Name input field", type="Text", validationRules="", styling=InputFieldStyling)
emailField: InputField = InputField(name="EmailField", description="Email input field", type="Email", validationRules="", styling=InputFieldStyling)
messageField: InputField = InputField(name="MessageField", description="Message input field", type="Text", validationRules="", styling=InputFieldStyling)
contactForm.inputFields.update({nameField, emailField, messageField})
ConstructionCompanyScreen: Screen = Screen(name="ConstructionCompanyScreen", description="Screen displaying construction company information", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", view_elements={header, section1, section2, learnMoreButton1, learnMoreButton2, footer, contactForm}, is_main_page=True, layout=ScreenLayout)
ConstructionCompanyModule: Module = Module(name="ConstructionCompanyModule", screens={ConstructionCompanyScreen})
gui_model: GUIModel = GUIModel(name="ConstructionCompanyApp", package="com.example.constructioncompany", versionCode="1", versionName="1.0", description="A web application for a construction company.", screenCompatibility=True, modules={ConstructionCompanyModule})
