from besser.BUML.notations.source_code_to_buml.output.buml.model import *
from besser.BUML.metamodel.structural import *
from besser.BUML.metamodel.gui import *

ScreenLayout = Layout(type=LayoutType.Flex, orientation="vertical", gap="15px", alignment=JustificationType.Center)
HeaderColor = Color(background_color="#1A202C", text_color="#FFFFFF", border_color="#1A202C")
HeaderPosition = Position(type=PositionType.Relative, alignment="center")
HeaderSize = Size(width="100%", height="64px", unit_size=UnitSize.PIXELS)
HeaderStyling = Styling(size=HeaderSize, position=HeaderPosition, color=HeaderColor)
NavColor = Color(background_color="#1A202C", text_color="#FFFFFF", border_color="#1A202C")
NavPosition = Position(type=PositionType.Relative, alignment="center")
NavSize = Size(width="100%", height="64px", unit_size=UnitSize.PIXELS)
NavStyling = Styling(size=NavSize, position=NavPosition, color=NavColor)
MainContentColor = Color(background_color="#2D3748", text_color="#FFFFFF", border_color="#2D3748")
MainContentPosition = Position(type=PositionType.Relative, alignment="center")
MainContentSize = Size(width="100%", height="auto", unit_size=UnitSize.PIXELS)
MainContentStyling = Styling(size=MainContentSize, position=MainContentPosition, color=MainContentColor)
FooterColor = Color(background_color="#1A202C", text_color="#FFFFFF", border_color="#1A202C")
FooterPosition = Position(type=PositionType.Relative, alignment="center")
FooterSize = Size(width="100%", height="auto", unit_size=UnitSize.PIXELS)
FooterStyling = Styling(size=FooterSize, position=FooterPosition, color=FooterColor)
ButtonColor = Color(background_color="#38A169", text_color="#FFFFFF", border_color="#2F855A")
ButtonPosition = Position(type=PositionType.Relative)
ButtonSize = Size(width="auto", height="40px", padding="8px", margin="8px", font_size="14px", unit_size=UnitSize.PIXELS)
ButtonStyling = Styling(size=ButtonSize, position=ButtonPosition, color=ButtonColor)
InputFieldColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="#CBD5E0")
InputFieldPosition = Position(type=PositionType.Relative)
InputFieldSize = Size(width="100%", height="40px", padding="8px", margin="8px", font_size="14px", unit_size=UnitSize.PIXELS)
InputFieldStyling = Styling(size=InputFieldSize, position=InputFieldPosition, color=InputFieldColor)
headerLogo: Image = Image(name=WebPage_headerLogo.name, description="Law Firm Logo", styling=HeaderStyling)
navMenu: Menu = Menu(name="NavigationMenu", description="Main navigation menu", menuItems={MenuItem(label="Home"), MenuItem(label="About"), MenuItem(label="Services"), MenuItem(label="Contact")}, styling=NavStyling)
aboutUsSection: ViewComponent = ViewComponent(name=WebPage_mainAboutUsTitle.name, description="About Us Section", styling=MainContentStyling)
ourServicesSection: ViewComponent = ViewComponent(name=WebPage_mainOurServicesTitle.name, description="Our Services Section", styling=MainContentStyling)
contactUsSection: ViewComponent = ViewComponent(name=WebPage_mainContactUsTitle.name, description="Contact Us Section", styling=MainContentStyling)
footerCopyright: ViewComponent = ViewComponent(name=WebPage_footerCopyright.name, description="Copyright Information", styling=FooterStyling)
footerSocialLinks: Menu = Menu(name=WebPage_footerSocialLinks.name, description="Social Media Links", menuItems={MenuItem(label="Facebook"), MenuItem(label="Twitter"), MenuItem(label="Instagram")}, styling=FooterStyling)
newsletterForm: Form = Form(name="NewsletterForm", description="Sign up for our newsletter", inputFields={InputField(name=WebPage_footerNewsletterEmail.name, description="Email Input Field", type="Email", validationRules="required", styling=InputFieldStyling)}, styling=FooterStyling)
newsletterSubmitButton: Button = Button(name="NewsletterSubmitButton", description="Submit Newsletter Form", label="Submit", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Submit, styling=ButtonStyling)
WebPageScreen: Screen = Screen(name="WebPageScreen", description="Main web page screen", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", view_elements={headerLogo, navMenu, aboutUsSection, ourServicesSection, contactUsSection, footerCopyright, footerSocialLinks, newsletterForm, newsletterSubmitButton}, is_main_page=True, layout=ScreenLayout)
WebPageModule: Module = Module(name="WebPageModule", screens={WebPageScreen})
gui_model: GUIModel = GUIModel(name="WebPageApp", package="com.example.webpageapp", versionCode="1", versionName="1.0", description="A web page for a law firm.", screenCompatibility=True, modules={WebPageModule})
