HeaderSize = Size(width="100%", height="auto", padding="16px", margin="0", font_size="16", unit_size=UnitSize.PIXELS)
HeaderStyling = Styling(size=HeaderSize, position=HeaderPosition, color=HeaderColor)
ButtonColor = Color(background_color="#007BFF", text_color="#FFFFFF", border_color="#0056b3")
ButtonPosition = Position(type=PositionType.Relative, alignment="Center", z_index=10)
ButtonSize = Size(width="auto", height="40", padding="8px", margin="24", font_size="14", unit_size=UnitSize.PIXELS)
ButtonStyling = Styling(size=ButtonSize, position=ButtonPosition, color=ButtonColor)
MainColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="")
MainPosition = Position(type=PositionType.Relative, alignment="Center", z_index=0)
MainSize = Size(width="100%", height="auto", padding="16px", margin="0", font_size="16", unit_size=UnitSize.PIXELS)
MainStyling = Styling(size=MainSize, position=MainPosition, color=MainColor)
FooterColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="")
FooterPosition = Position(type=PositionType.Relative, alignment="Center", z_index=0)
FooterSize = Size(width="100%", height="auto", padding="16px", margin="0", font_size="16", unit_size=UnitSize.PIXELS)
FooterStyling = Styling(size=FooterSize, position=FooterPosition, color=FooterColor)
viewComponent: ViewComponent = ViewComponent(name="BankingSiteView", description="Banking and finance site with login functionality")
header: ViewComponent = ViewComponent(name="Header", description="Site header with navigation", styling=HeaderStyling)
navMenu: Menu = Menu(name="NavigationMenu", description="Main navigation menu", menuItems={MenuItem(label="Home"), MenuItem(label="About"), MenuItem(label="Services"), MenuItem(label="Contact")}, styling=HeaderStyling)
mainSection: ViewComponent = ViewComponent(name="MainSection", description="Main content area", styling=MainStyling)
welcomeMessage: ViewComponent = ViewComponent(name="WelcomeMessage", description="Welcome message for users", styling=MainStyling)
learnMoreButton: Button = Button(name="LearnMoreButton", description="Button to learn more about services", label="Learn More", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Navigate, styling=ButtonStyling)
loginSection: ViewComponent = ViewComponent(name="LoginSection", description="Login form for users", styling=MainStyling)
loginForm: Form = Form(name="LoginForm", description="Form for user login", inputFields={InputField(name="Username", description="Username input field", type=InputFieldType.Text, validationRules="", styling=MainStyling), InputField(name="Password", description="Password input field", type=InputFieldType.Password, validationRules="", styling=MainStyling)}, styling=MainStyling)
loginButton: Button = Button(name="LoginButton", description="Button to submit login form", label="Login", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.SubmitForm, styling=ButtonStyling)
footer: ViewComponent = ViewComponent(name="Footer", description="Site footer", styling=FooterStyling)
BankingSiteScreen: Screen = Screen(name="BankingSiteScreen", description="Main screen for the banking site", x_dpi=Property(name="x_dpi", type=IntegerType), y_dpi=Property(name="y_dpi", type=IntegerType), screen_size=Property(name="screen_size", type=StringType), view_elements={header, navMenu, mainSection, welcomeMessage, learnMoreButton, loginSection, loginForm, loginButton, footer}, is_main_page=True, layout=ScreenLayout)
BankingModule: Module = Module(name="BankingModule", screens={BankingSiteScreen})
gui_model: GUIModel = GUIModel(name="BankingApp", package="com.example.bankingapp", versionCode=Property(name="versionCode", type=StringType), versionName=Property(name="versionName", type=StringType), description="A web application for banking and finance services.", screenCompatibility=True, modules={BankingModule})
