HeaderSize = Size(width="100%", height="auto", padding="10px", margin="0", font_size="40px", unit_size=UnitSize.PIXELS)
HeaderStyling = Styling(size=HeaderSize, position=HeaderPosition, color=HeaderColor)
ButtonColor = Color(background_color="#EC4899", text_color="#FFFFFF", border_color="")
ButtonPosition = Position(type=PositionType.Relative, alignment="center")
ButtonSize = Size(width="auto", height="40px", padding="10px", margin="10px", font_size="16px", unit_size=UnitSize.PIXELS)
ButtonStyling = Styling(size=ButtonSize, position=ButtonPosition, color=ButtonColor)
ImagePosition = Position(type=PositionType.Relative, alignment="center")
ImageSize = Size(width="300px", height="200px", unit_size=UnitSize.PIXELS)
ImageStyling = Styling(size=ImageSize, position=ImagePosition)
FooterColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="")
FooterPosition = Position(type=PositionType.Static, alignment="center")
FooterSize = Size(width="100%", height="auto", padding="10px", margin="0", font_size="16px", unit_size=UnitSize.PIXELS)
FooterStyling = Styling(size=FooterSize, position=FooterPosition, color=FooterColor)
FormColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="")
FormPosition = Position(type=PositionType.Relative, alignment="center")
FormSize = Size(width="100%", height="auto", padding="20px", margin="10px", unit_size=UnitSize.PIXELS)
FormStyling = Styling(size=FormSize, position=FormPosition, color=FormColor)
InputFieldSize = Size(width="100%", height="40px", padding="10px", margin="10px", font_size="14px", unit_size=UnitSize.PIXELS)
InputFieldStyling = Styling(size=InputFieldSize)
header: ViewComponent = ViewComponent(name="Header", description="Charity Organization Header", styling=HeaderStyling)
charityImage: Image = Image(name="CharityImage", styling=ImageStyling, description="")
donateButton: Button = Button(name="DonateButton", description="Donate Now", label="Donate Now", buttonType=ButtonType.RaisedButton, actionType=ButtonActionType.Add, styling=ButtonStyling)
footer: ViewComponent = ViewComponent(name="Footer", description="Charity Organization Footer", styling=FooterStyling)
volunteerForm: Form = Form(name="VolunteerForm", description="Volunteer Form", inputFields={InputField(name="NameField", description="Name", type="Text", styling=InputFieldStyling), InputField(name="EmailField", description="Email", type="Email", styling=InputFieldStyling)}, styling=FormStyling)
CharityOrganizationScreen: Screen = Screen(name="CharityOrganizationScreen", description="Charity Organization Main Screen", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", view_elements={header, charityImage, donateButton, footer, volunteerForm}, is_main_page=True, layout=ScreenLayout)
CharityModule: Module = Module(name="CharityModule", screens={CharityOrganizationScreen})
gui_model: GUIModel = GUIModel(name="CharityApp", package="com.example.charityapp", versionCode="1", versionName="1.0", description="A web application for a charity organization.", screenCompatibility=True, modules={CharityModule})
