from besser.BUML.notations.source_code_to_buml.output.buml.model import *
from besser.BUML.metamodel.structural import *
from besser.BUML.metamodel.gui import *

ScreenLayout = Layout(type=LayoutType.Flex, orientation="vertical", gap="15px", alignment=JustificationType.Center)
HeaderColor = Color(background_color="#4F46E5", text_color="#FFFFFF", border_color="")
HeaderPosition = Position(type=PositionType.Relative)
HeaderSize = Size(width="100%", height="auto", padding="16px", font_size="24", unit_size=UnitSize.PIXELS)
HeaderStyling = Styling(size=HeaderSize, position=HeaderPosition, color=HeaderColor)
NavMenuColor = Color(text_color="#FFFFFF", background_color="", border_color="")
NavMenuPosition = Position(type=PositionType.Inline)
NavMenuSize = Size(padding="8px", margin="16px", font_size="16", unit_size=UnitSize.PIXELS)
NavMenuStyling = Styling(size=NavMenuSize, position=NavMenuPosition, color=NavMenuColor)
SearchInputColor = Color(background_color="#FFFFFF", text_color="#000000", border_color="#E5E7EB")
SearchInputPosition = Position(type=PositionType.Relative)
SearchInputSize = Size(width="200px", padding="8px", font_size="14", unit_size=UnitSize.PIXELS)
SearchInputStyling = Styling(size=SearchInputSize, position=SearchInputPosition, color=SearchInputColor)
SectionColor = Color(background_color="#FFFFFF", text_color="#1F2937", border_color="#E5E7EB")
SectionPosition = Position(type=PositionType.Relative)
SectionSize = Size(width="100%", padding="16px", margin="32px", font_size="16", unit_size=UnitSize.PIXELS)
SectionStyling = Styling(size=SectionSize, position=SectionPosition, color=SectionColor)
header: ViewComponent = ViewComponent(name=WebPage_headerTitle.name, description="Main header with logo and navigation", styling=HeaderStyling)
navMenu: Menu = Menu(name="NavigationMenu", description="Main navigation menu", menuItems={MenuItem(label=WebPage_homeLink.name), MenuItem(label=WebPage_aboutLink.name), MenuItem(label=WebPage_servicesLink.name), MenuItem(label=WebPage_contactLink.name)}, styling=NavMenuStyling)
searchInput: InputField = InputField(name="SearchInput", description="Search input field", type="Text", validationRules="", styling=SearchInputStyling)
aboutSection: ViewComponent = ViewComponent(name=WebPage_aboutTitle.name, description="About us section", styling=SectionStyling)
servicesSection: ViewComponent = ViewComponent(name=WebPage_servicesTitle.name, description="Our services section", styling=SectionStyling)
contactSection: ViewComponent = ViewComponent(name=WebPage_contactTitle.name, description="Contact us section", styling=SectionStyling)
WebPageScreen: Screen = Screen(name="WebPageScreen", description="Main web page screen with header, navigation, and sections", x_dpi="x_dpi", y_dpi="y_dpi", screen_size="Medium", view_elements={header, navMenu, searchInput, aboutSection, servicesSection, contactSection}, is_main_page=True, layout=ScreenLayout)
WebPageModule: Module = Module(name="WebPageModule", screens={WebPageScreen})
gui_model: GUIModel = GUIModel(name="WebPageApp", package="com.example.webpageapp", versionCode="1", versionName="1.0", description="A web application for a banking institution.", screenCompatibility=True, modules={WebPageModule})
